import React, { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { CardsContext } from '../contexts/CardsContext';
function Card(props) {

  const userContext = React.useContext(CurrentUserContext);
  const cardsContext = React.useContext(CardsContext);
    // Определяем, являемся ли мы владельцем текущей карточки
  const isOwn = props.card.owner._id === userContext._id;

    // Создаём переменную, которую после зададим в `className` для кнопки удаления
  const cardDeleteButtonClassName = (
    `element__delete ${isOwn ? 'element__delete_active' : 'element__delete'}`
  );

    // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
  const isLiked = props.card.likes.some(i => i._id === userContext._id);

    // Создаём переменную, которую после зададим в `className` для кнопки лайка
  const cardLikeButtonClassName = (
    `element__like ${isLiked ? 'element__like_active' : 'element__like'}`
  )

  const isCard = props.card.likes.some(i => i._id === userContext._id);
  const cardDeleteClassName = (
    `element ${isOwn ? 'element__delete_active' : 'element__delete'}`
  );

  function handleClick() {
    props.onCardClick(props.card);
  }

  function handleLikeClick() {
    props.onCardLike(props.card)
  }

  function handleDeleteClick() {
    props.onCardDelete(props.card)
  }

    return (
            <div className="element">
                <img className="element__img" src={props.card.link} alt={props.card.name} onClick ={handleClick}/>
                <div className="element__info">
                    <button className={cardDeleteButtonClassName} type="button" aria-label="Удалить" onClick={handleDeleteClick}></button>
                    <h2 className="element__name">{props.card.name}</h2>
                    <div className="element__like-group">
                        <button className={cardLikeButtonClassName} type="button" aria-label="Лайк" onClick={handleLikeClick}></button>
                        <p className="element__like-counter">{props.card.likes.length}</p>
                    </div>
                </div>
            </div>
            )
}

export default Card;
