import Header from './Header.js'
import Main from './Main.js'
import Footer from './Footer.js'
import PopupWithForm from './PopupWithForm.js'
import ImagePopup from './ImagePopup.js'
import React, {useState, useEffect } from 'react';
import { api } from '../utils/Api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { CardsContext } from '../contexts/CardsContext';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';



function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState();
  const [currentUser, setCurrentUser] = useState('');
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api.getUserData()
    .then((res) => {
      setCurrentUser(res)
    })
    .catch((err) => {
      console.log(err)
    })
  }, [])

  useEffect(() => {
    api.getCards()
    .then((res) => {
        setCards(res)
    })
    .catch((err) => {
      console.log(err)
    })
}, [])

function handleCardLike(card) {

  // Снова проверяем, есть ли уже лайк на этой карточке
  //const isLiked = card.likes.some(i => i._id === userContext._id);

  // Отправляем запрос в API и получаем обновлённые данные карточки
  //api.addLike(card._id, !isLiked)
  //.then((newCard) => {
    //setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
  //});
}

function handleCardDelete() {

}

function handleEditAvatarClick() {
  setIsEditAvatarPopupOpen(true);
}

function handleEditProfileClick() {
  setIsEditProfilePopupOpen(true);
}

function handleAddPlaceClick(){
  setIsAddPlacePopupOpen(true);
}

function handleCardClick(card) {
  setSelectedCard(card);
}

function closeAllPopups() {
  setIsEditAvatarPopupOpen(false);
  setIsEditProfilePopupOpen(false);
  setIsAddPlacePopupOpen(false);
  setSelectedCard();


}

function handleUpdateUser(data) {
  api.patchUserData(data.name, data.about)
  .then((res) => {
    setCurrentUser(res)
  })
  .catch((err) => {
    console.log(err)
  })
}

function handleUpdateAvatar(data) {
  console.log(data)
  api.patchAvatar(data.avatar)
  .then((res) => {
    setCurrentUser(res)
  })
  .catch((err) => {
    console.log(err)
  })
}

function handleUpdateCard(data) {
  console.log(data)
  api.patchCard(data.name, data.link)
  .then((res) => {
    setCards([res, ...cards]);
  })
  .catch((err) => {
    console.log(err)
  })
}

  return(


    <div className="page">
      <CurrentUserContext.Provider value={currentUser}>

      <Header />

      <CardsContext.Provider value={cards}>

      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}
      onCardClick ={handleCardClick} cards={cards} onCardLike={handleCardLike} onCardDelete={handleCardDelete} />

      <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />

      <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleUpdateCard} />

      <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />

      <ImagePopup onClose={closeAllPopups}
      card={selectedCard} />

      </CardsContext.Provider>

      <Footer />
      </CurrentUserContext.Provider>
    </div>

  )
}

export default App;
