import {api} from '../utils/Api.js'
import React, { useEffect, useState } from 'react';
import Card from './Card.js'
import { CurrentUserContext } from '../contexts/CurrentUserContext'

function Main(props) {

    /*const [userName, setUserName] = useState();
    const [userDescription, setUserDescription] = useState();
    const [userAvatar, setUserAvatar] = useState();
    */
    const userContext = React.useContext(CurrentUserContext);

    /*useEffect(() => {
        api.getUserData()
        .then((res) => {
            setUserName(res.name);
            setUserDescription(res.about);
            setUserAvatar(res.avatar)
        })
        .catch((err) => {
          console.log(err)
        })
    }, [])
*/
    const [cards, setCards] = useState([])

    useEffect(() => {
        api.getCards()
        .then((res) => {
            setCards(res)
        })
        .catch((err) => {
          console.log(err)
        })
    }, [])

    /*
src={userContext.avatar}
{userContext.name}
    */
    return (
    <>
        <section className="profile">
            <div className="profile__avatar">
                <div className="profile__overlay">
                    <img className="profile__image"
                    alt ="Аватар профиля" onClick={props.onEditAvatar}/>
                </div>
            </div>
            <div className="profile__info">
                <div className="profile__text">
                    <h1 className="profile__name" >
                    </h1>
                    <p className="profile__description">{userContext.about}
                    </p>
                </div>
                <button className="profile__edit-button" type="button" aria-label="Редактирование профиля" onClick={props.onEditProfile}></button>
            </div>
            <button className="profile__add-button" type="button" aria-label="Добавление фотографий" onClick={props.onAddPlace}></button>
        </section>
        <section className="elements">
        {cards.map((item) => (
            <Card onCardClick={props.onCardClick} key={item._id}  card={item}/>
        ))}
        </section>
    </>
    )
}

export default Main;
