import React from "react";
import Module from "./Module";
import { Link } from 'react-router-dom';
import * as auth from './AuthApi';
import { useNavigate } from 'react-router-dom';
import InfoTooltip from "./InfoTooltip"

function Register(props) {

  const[pass, setPassword] = React.useState("");
  const[email, setEmail] = React.useState("");
  cp

  let navigate = useNavigate();

  function handleChangePassword(e) {
    setPassword(e.target.value);
  }

  function handleChangeValue(e) {
    setEmail(e.target.value);
  }


  function handleSubmit(e) {
    e.preventDefault()
    auth.register(pass, email)
    .then(() => {
      navigate('/sign-in')
      {<InfoTooltip isOpen="popup_opened" onSubmit={true}/>}
    })
    .catch(() => {
      <InfoTooltip isOpen="popup_opened" onSubmit={false}/>
    })
  }

  return (
    <Module
      headerButton="Войти"
      name="Регистрация"
      submitButton="Зарегистрироваться"
      link={'/sign-in'}
      passChange={handleChangePassword}
      emailChange={handleChangeValue}
      valuePass={pass}
      valueEmail={email}
      onSubmit={handleSubmit}
    >

      <Link to='/sign-in' className="loginPage__subSubmitButton">Уже зарегистрированы? Войти</Link>
    </Module>
  )
}

export default Register;
